function showAlert(n){var t=$(template(n));alertContainer.append(t);window.setTimeout(function(){t.fadeTo(1500,0).slideUp(500,function(){t.remove()})},2e3)}function success(n){showAlert({alertClass:"alert-success",message:n})}function info(n){showAlert({alertClass:"alert-info",message:n})}function warning(n){showAlert({alertClass:"alert-warning",message:n})}function error(n){showAlert({alertClass:"alert-danger",message:n})}var alertService={showAlert:showAlert,success:success,info:info,warning:warning,error:error},alertContainer,template;window.alerts=alertService;alertContainer=$(".alert-container");template=_.template("<div class='alert <%= alertClass %> alert-dismissable'>"+"<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;<\/button>"+"<%= message %>"+"<\/div>");